export type RkbImage = {
  uuid?: string;
  mime: string;
  data: string;
  width: number;
  height: number;
  size?: number;
};

const uploadPicture = ({
  image,
  user,
  token,
}: {
  image: RkbImage;
  user: string;
  token: string;
}) => {
  if (_.isEmpty(image))
    return api.reject(api.E_INVALID_ARGUMENT, 'missing parameter: image');
  if (!user)
    return api.reject(api.E_INVALID_ARGUMENT, 'missing parameter: user');
  if (!token)
    return api.reject(api.E_INVALID_ARGUMENT, 'missing parameter: token');

  const headers = api.headers('octet-stream', {
    'X-CSRF-Token': token,
    'Content-Disposition': `file;filename="${user}-${new Date().getTime()}.${image.mime.replace(
      'image/',
      '',
    )}"`,
  });

  return api.callHttp(
    `${api.httpUrl(
      api.path.uploadFile,
    )}/user/user/user_picture?_format=hal_json`,
    {
      method: 'POST',
      headers,
      body: Buffer.from(image.data, 'base64'),
    },
    toFile,
  );
};

// get my coupons
const getMyCoupon = ({token}: {token: string}) => {
  return api.callHttpGet(
    `${api.httpUrl(api.path.rokApi.rokebi.coupon)}/0?_format=json`,
    toCoupon,
    api.withToken(token, 'json'),
  );
};

export default {
  toAccount,
  toFile,
  getAccount,
  getCashHistory,
  getCashExpire,
  validateActCode,
  getByUser,
  registerMobile,
  uploadPicture,
  getMyCoupon,
};
